function wysifig(fig, width, height)% function wysifig(fig, width, height)%% wysifig ... positions a figure on the top right--hand side of% the screen with the dimensions of a printed pageif nargin < 1    fig = figure;endif nargin < 2 || isempty(width)    width = 8.5*2.54;endif nargin < 3 || isempty(height)    height = 11*2.54;endset(fig, ...    'PaperUnits',       'centimeters', ...    'PaperPosition',    [1 1 width height], ...    'Units',            'centimeters');r      = get(fig, 'Position');r(2:3) = [width height];set(fig, 'Position', r);wysiwyg;